function Clock() {

    function getTime() {
        const { date } = this.state;
        const h = `0${date.getHours()}`.slice(-2);
        const m = `0${date.getMinutes()}`.slice(-2);
        const s = `0${date.getSeconds()}`.slice(-2);
        return `${h}:${m}:${s}`;
    }

    const time = this.getTime();
    return (
        <div className="clock">
            It's {time}.
        </div>
    )

}

export default Clock;
